/**
 * @brief Get the I2S_CONFIG APP version.
 * @return DAVE_APP_VERSION_t APP version information (major, minor and patch number)
 */
DAVE_APP_VERSION_t I2S_CONFIG_GetAppVersion()
{
  DAVE_APP_VERSION_t version;

  version.major = (uint8_t)I2S_CONFIG_MAJOR_VERSION;
  version.minor = (uint8_t)I2S_CONFIG_MINOR_VERSION;
  version.patch = (uint8_t)I2S_CONFIG_PATCH_VERSION;

  return (version);
}

/**
 * @brief Function to initialize the i2s module as per the configuration made
 * in GUI.
 * @param[in] handle Pointer to static content of APP configuration.
 * @return I2S_CONFIG_STATUS_t: Status of the i2s driver initialization.\n
 *   @ref I2S_CONFIG_STATUS_SUCCESS - on successful initialization.\n
 *   @ref I2S_CONFIG_STATUS_FAILURE - if initialization fails.\n
 *
 */
I2S_CONFIG_STATUS_t I2S_CONFIG_Init(const I2S_CONFIG_t *const handle)
{
  XMC_ASSERT("I2S_CONFIG APP handle function pointer uninitialized", (((handle != NULL) &&
             (handle->config != NULL)) && ((handle->config->i2s_config_init_func != NULL))));

  handle->config->i2s_config_init_func();

  return (I2S_CONFIG_STATUS_SUCCESS);
}

